import React from 'react'
import Axios from 'axios'
import { Form, Icon, Input, Button, Checkbox, Card } from 'antd'
import { Link } from 'react-router-dom'
import { Layout, Menu, Breadcrumb } from 'antd'
import 'antd/dist/antd.css'
import PLACE_SERVICE, { placeService } from '../../services/post-place'
import { MyContext } from '../../context'
const { Header, Content, Footer } = Layout

const baseURL =
  process.env.NODE_ENV === 'production' ? 'here should be your production endpoint' : 'http://localhost:3000/api'

class Place extends React.Component {
  state = {
    unplan: {},
    user: {},
    places: [],
    lugar: undefined
  }

  handleInput = e => {
    const { user } = this.state
    const key = e.target.name
    user[key] = e.target.value
    this.setState({ user })
    console.log(this.state.user)
    console.log(e.target.name)
  }

  onSubmit = e => {
    e.preventDefault()
    Axios.get(`${baseURL}/places/${this.state.places.id}`) //
      .then(({ data }) => {
        //
        console.log('2222222222222222222222', data)
        this.setState({
          //
          lugares: data.place,
          lugar: (
            <Card>
              <p>{'kjscbkja'}</p>
              <p>{'kajcbikjbc'}</p>
            </Card>
          )
        }) //
      }) //
      .catch(err => {
        //
        console.log(err) //
      }) //

    let { user } = this.state
    console.log('onsubmit', user)
    placeService(user)
      .then(res => {
        console.log('.......', res)
        this.setState({
          //
          lugares: res.place,
          lugar: (
            <Card>
              <p>
                name:
                {res.place.name}
              </p>
              <p>address: {res.place.address}</p>
            </Card>
          )
        })
      })

      .catch(err => console.log('el error en onsubmit', err))
  }

  clickCard = () => {
    console.log('lol')
  }

  cardPlace = () => {
    const { places } = this.state
    console.log(places)
    return places.map(places => {
      return (
        <div style={{ background: '#ECECEC', padding: '30px' }} onClick={this.cl}>
          <Card bordered={false} style={{ width: 300 }}>
            <h1>{places.name}</h1>
            <p>{places.address}</p>
          </Card>
        </div>
      )
    })
  }

  componentDidMount() {
    if (!localStorage.getItem('user')) return this.props.history.push('/login')
    Axios.get(`${baseURL}/selectedPlan/${this.props.match.params.id}`)
      .then(({ data }) => {
        this.setState({
          unplan: data.plan
        })
      })
      .catch(err => {
        console.log(err)
      })

    Axios.get(`${baseURL}/places/`)
      .then(res => {
        console.log('este es el que quiere ver emiliano', res.data)
        this.setState({
          places: res.data.place
        })
      })
      .catch(err => {
        console.log(err)
      })
  }

  render() {
    const { unplan } = this.state
    const { places } = this.state
    console.log(this.state)
    if (!unplan) return <p>Loading data...</p>
    //this.props.match.params.id
    return (
      <div style={{ display: 'flex', justifyContent: 'space-around' }}>
        <Layout>
          <Header style={{ position: 'fixed', zIndex: 1, width: '100%' }}>
            <div className="logo" />
            <Menu theme="dark" mode="horizontal" defaultSelectedKeys={['2']} style={{ lineHeight: '64px' }}>
              <Menu.Item key="1">nav 1</Menu.Item>
              <Menu.Item key="2">nav 2</Menu.Item>
              <Menu.Item key="3">nav 3</Menu.Item>
            </Menu>
          </Header>
          <Content style={{ padding: '0 50px', marginTop: 64 }}>
            <Breadcrumb style={{ margin: '16px 0' }}>
              <Breadcrumb.Item>Put your address to receive your food plan</Breadcrumb.Item>
            </Breadcrumb>
            <div>
              <div style={{ background: '#fff', padding: 24, minHeight: 380, display: 'flex' }}>
                <Card style={{ width: 300 }}>
                  <h1>{unplan.name}</h1>
                  <p>
                    Price:<br></br>
                    {unplan.price}
                  </p>
                  <p>
                    Tipo:<br></br>
                    {unplan.role}
                  </p>
                  <p>{unplan.description}</p>
                  <img src={unplan.image} alt="" />
                </Card>
                <div>{this.state.lugar}</div>
                <div>{this.cardPlace()}</div>
              </div>

              <p>{unplan.name}</p>
              <Form onSubmit={this.onSubmit} className="login-form">
                <Form.Item>
                  <Input
                    onChange={this.handleInput}
                    name="name"
                    prefix={<Icon type="user" style={{ color: 'rgba(0,0,0,.25)' }} />}
                    placeholder="Address name"
                  />
                </Form.Item>
                <Form.Item>
                  <Input
                    onChange={this.handleInput}
                    name="address"
                    prefix={<Icon type="user" style={{ color: 'rgba(0,0,0,.25)' }} />}
                    placeholder="Put your address here"
                  />
                  <Form.Item>
                    <Input type="submit" value="Submit" />
                  </Form.Item>
                </Form.Item>
              </Form>
              <Link to={`/pedidoCreate/${unplan._id}`}>
                <button>Add</button>
              </Link>
            </div>
          </Content>
        </Layout>
      </div>
    )
  }
}

export default Place
