import React, { Component } from 'react'
import PLACE_SERVICE from '../../services/post-place'

export default class Edit extends Component {
  state = {
    places: {}
  };

  componentDidMount (){
    this.setState({places: this.state.places})
    console.log(this.state)
  }

  handleInput = (e) => {
    const { places } = this.state;

    if (e.target.files) places.img = e.target.files[0]
    else {
    const key = e.target.name;
    places[key] = e.target.value;
  }
    this.setState({ places });
    console.log('<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<',this.state.places)
  };

  onSubmit = (e) => {
    e.preventDefault()



    const fd = new FormData()
    for (const key in this.state.places){fd.append(key, this.state.places[key])

      PLACE_SERVICE.onEdit(this.props.match.params.id,fd)

        .then(res => {

          this.props.history.push(`/places/${this.props.match.params.id}`)
        })
        .catch(e => console.log(e));

  }
  }


  render() {
const {places} = this.state

    return (
      <div>
          <Form onSubmit={this.onSubmit} className="login-form">
                <Form.Item>
                  <Input
                    onChange={this.handleInput}
                    name="name"
                    prefix={<Icon type="user" style={{ color: 'rgba(0,0,0,.25)' }} />}
                    placeholder="Address name"
                  />
                </Form.Item>
                <Form.Item>
                  <Input
                    onChange={this.handleInput}
                    name="address"
                    prefix={<Icon type="user" style={{ color: 'rgba(0,0,0,.25)' }} />}
                    placeholder="Put your address here"
                  />
                  <Form.Item>
                    <Input type="submit" value="Submit" />
                  </Form.Item>
                </Form.Item>
              </Form>
      </div>
    )
  }
}
