  state = {
    place: {}
  };

  componentDidMount (){
    this.setState({place: this.state.place})
    console.log(this.state)
  }

  handleInput = (e) => {
    const { place } = this.state;

    if (e.target.files) place.img = e.target.files[0]
    else {
    const key = e.target.name;
    place[key] = e.target.value;
  }
    this.setState({ place });
