import React from 'react'
import Axios from 'axios'
import { Form, DatePicker, TimePicker, Card } from 'antd';
import 'antd/dist/antd.css'
import  { pedidoService } from '../../services/post-date'
import PLACE_SERVICE from '../../services/post-place'



const baseURL = process.env.NODE_ENV === 'production'
  ? 'aqui va lo de production'
  :  'http://localhost:3000/api'

  class Pedido extends React.Component{
    state ={
      pedido:{
        fecha:'',
        hora:''
      },
      unplan:{},
      user:{}
    }
    handleInput = e => {
      const { user } = this.state
      const key = e.target.name
      user[key] = e.target.value
      this.setState({ user })
      console.log(this.state.user)
      console.log(e.target.name)
    }


    //Datepicker method
  dateOnChange = (date, dateString) => {
    console.log(dateString);
    let {pedido}= this.state
    pedido['fecha']=dateString
    this.setState({pedido})
  }
  //Timepicker methods
  timeOnChange = (time, timeString) => {
    console.log(timeString);
    let {pedido}= this.state
    pedido['hora']=timeString
    this.setState({pedido})
  }


    onSubmit = e => {
      e.preventDefault()
      let{user} = this.state
      console.log('onsubmit', user)
      pedidoService(user)
      .then(res => console.log('la respuesta en onsubment', res))

      .catch(err => console.log('el error en onsubmit', err))
    }

    onSubmit = (e) => {
      e.preventDefault();
      const fd = new FormData()
          fd.append('mediaPath', this.state.mediaPath)
          for(const key in this.state.pedido){
            fd.append(key, this.state.pedido[key])
          }
      console.log(this.state.pedido)
      PLACE_SERVICE.onSendDate(fd)
        .then((e) => {
          console.log(this.state.pedido)
          console.log('data sent to backend, todo cool, todo chido...')
          this.props.history.push('/pedidoCreate');
        })
        .catch((error) => {
          console.log(error);
        });

    };


    componentDidMount() {
      Axios.get(`${baseURL}/selectedPlan/${this.props.match.params.id}`)
        .then(({ data }) => {
          this.setState({
            unplan: data.plan
          })
        })
        .catch(err => {
          console.log(err)
        })

    }


          render() {
            const { unplan } = this.state
            const dateFormatList = ['DD/MM/YYYY', 'DD/MM/YY'];
    if (!unplan) return <p>Loading data...</p>
            return (
              <div style={{ display: 'flex', marginTop: '3vw', justifyContent: 'space-around' }}>
        <Card style={{ width: 300, height:'400px', backgroundImage: `url(${unplan.image})`, color:'white', backgroundPosition:'center' }}>
          <div className='pedido-plan'>
            <div style={{fontSize:'20px'}}>
          <p>{unplan.name}</p>

          <p>Price:<br/>{unplan.price}</p>

          <p>Type:<br/>{unplan.role}</p>
          <p>{unplan.description}</p>
          </div>
          </div>

        </Card>
        <p>{unplan.name}</p>

        <Form onSubmit={this.onSubmit}>
              <Form.Item name="fecha" label="DatePicker">
          <DatePicker name="fecha" onChange={this.dateOnChange} format={dateFormatList}/>
        </Form.Item>
        <Form.Item name="hora" label="TimePicker">
        <TimePicker name="hora" onChange={this.timeOnChange} />
      </Form.Item>
      </Form>
      </div>
            );
          }
        }

     export default Pedido
