import React from 'react'
import Axios from 'axios'
import { Card} from 'antd'
import { Link } from 'react-router-dom'
import '../index.css'

const baseURL =
  process.env.NODE_ENV === 'production' ? 'here should be your production endpoint' : 'http://localhost:3000/api'

class Plans extends React.Component {
  state = {
    plans: [],
    user: {}
  }

  componentDidMount() {
    console.log('mounting')
    //if (!this.state.user.loggedUser) return this.props.history.push('/login');
    Axios.get(`${baseURL}/plans`)
      .then(res => {
        console.log(res.data)
        this.setState({
          plans: res.data.plans
        })
        console.log(this.state)
      })
      .catch(err => {
        console.log(err)
      })
  }

  cardPlan = () => {
    const { plans } = this.state
    return plans.map((plan, key) => {
      return (
        <div style={{ background: '#ECECEC', padding: '30px' }}>
          <Link
            onClick={() => {
              localStorage.setItem('plan', JSON.stringify(plan._id))
            }}
            to={`/places/${plan._id}`}
          >
            <Card bordered={false} style={{ width: 300, backgroundImage: `url(${plan.image})`, height: '400px', fontSize:'20px', backgroundPosition:'center', color:'white'}} >
             <div className='h3'>
              <h3>{plan.name}</h3>
              </div>
              <p>Price:<br></br>{plan.price}</p>
              <p>Type:<br></br>{plan.role}</p>
              <p>{plan.description}</p>

            </Card>
          </Link>
        </div>
      )
    })
  }

  render() {
    return (
      <div className="plans">
        <h1>planes</h1>
        <div className='planes'>
        {this.cardPlan()}
        </div>
      </div>
    )
  }
}
export default Plans
