import React from 'react'
import Axios from 'axios'
import { Form, Icon, Input, Button, Checkbox } from 'antd'
import 'antd/dist/antd.css'

const baseURL =
  process.env.NODE_ENV === 'production' ? 'here should be your production endpoint' : 'http://localhost:3000'

class Place extends React.Component {
  state = {
    places: [],
    plans: []
  }

  componentDidMount() {
    Axios.get(`${baseURL}/${this.props.match.params.id}`)
      .then(({ res }) => {
        this.setState({
          plans: res
        })
        console.log(this.state)
      })
      .catch(err => {
        console.log(err)
      })
  }

  render() {
    const plans = this.state
    return (
      <div>
        <h1>place</h1>
        <p>{plans.name}</p>
        <Form onSubmit={this.handleSubmit} className="login-form">
          <Form.Item>
            <Input prefix={<Icon type="user" style={{ color: 'rgba(0,0,0,.25)' }} />} placeholder="Address name" />
          </Form.Item>
          <Form.Item>
            <Input
              prefix={<Icon type="user" style={{ color: 'rgba(0,0,0,.25)' }} />}
              placeholder="Put your address here"
            />
          </Form.Item>
        </Form>
      </div>
    )
  }
}

export default Place
