import React from 'react'
import Axios from 'axios'
import { Form, Icon, Input, Button, Checkbox, Card } from 'antd'
import { Layout, Menu, Breadcrumb } from 'antd'
import 'antd/dist/antd.css'
import PLACE_SERVICE, { placeService } from '../../services/post-place'
import { MyContext } from '../../context'
const { Header, Content, Footer } = Layout

const baseURL =
  process.env.NODE_ENV === 'production' ? 'here should be your production endpoint' : 'http://localhost:3000/api'

class Place extends React.Component {
  state = {
    unplan: {},
    user: {},
    places: {
      name: '',
      address: ''
    },
    form: {
      title: '',
      button: '', 
      endpoint: ''
    }
  }

  handleInput = e => {
    const { user } = this.state
    const key = e.target.name
    user[key] = e.target.value
    this.setState({ user })
    console.log(this.state.user)
    console.log(e.target.name)
  }



  onSubmit = (e) => {
    e.preventDefault();
    const { places, form } = this.state;
    if (form.title === 'Edit') {
      Axios.put(`http://localhost:3000/api/${form.endpoint}`, places)
      .then(({ data }) => {
        console.log(data);
        this.setState({
          places: {
            name: '',
            gender: '',
            ethnicity: '',
            image: '',
            physique: '',
            hair: ''
          }
        });
        this.props.history.push('/characters')
      })
      .catch((error) => {
        console.log(error);
      });
    } else {
    axios
      .post('http://localhost:3000/api/characters', character)
      .then(({ data }) => {
        console.log(data);
        this.setState({
          character: {
            name: '',
            gender: '',
            ethnicity: '',
            image: '',
            physique: '',
            hair: ''
          }
        });
        this.props.history.push('/characters')
      })
      .catch((error) => {
        console.log(error);
      });
    }
  };



  //esso hace que me jale la parte de guardar la informacion de los places en la base de datos
  //onSubmit = e => {
    //e.preventDefault()
    //let { user } = this.state
